
const findUser = (client, message, args, name, code) => {
    let r = code.split("$findUser[").length - 1

    let option = code.split("$findUser[")[r].split("]")[0]

    if(!option) return message.channel.send(":x: Nothing is provided inside `$findUser[]`")

    let user = client.users.cache.get(option) || message.mentions.users.first() || client.users.cache.find(user => user.username.toLowerCase() === option.toLowerCase()) || message.author

    code = code.replaceLast(`$findUser[${option}]`, user.id)

    return {
        code: code
    }
}

module.exports = findUser