const userAvatar = async (client, message, args, name, code) => {
  
  let r = code.split("$userAvatar[").length - 1
  
  let inside = code.split("$userAvatar[")[r].split("]")[0]
  
  let id = inside || message.author.id
  
  let user = await client.users.fetch(id).catch(err => {})
  
  let err = client.suppress.get(message.idd)

  if (!user && err === undefined) return message.channel.send(`❌ Invalid user ID in \`$userAvatar[${inside}]\``)
  else if (!user && err !== undefined) return message.channel.send(err).catch(err => {})
   
  code = code.replaceLast(`$userAvatar[${inside}]`, user.displayAvatarURL())
  
  return {
    code:code 
  } 
}

module.exports = userAvatar